import React, { useState, useEffect } from 'react'
import { Formik, Form, Field, withFormik } from 'formik'
import * as Yup from 'yup'
import axios from 'axios'

function AppForm({ values, errors, touched }) {
  return (
    <Formik>
      <div>
        <Form>
          {touched.name && errors.name && <p>{errors.email}</p>}
          <Field type='name' name='name' placeholder='Name' />
          <Field type='email' name='email' placeholder='Email' />
          <Field type='password' name='password' placeholder='Password' />
          <label>
            <Field type='checkbox' name='terms' placeholder='Password' />I agree
            to the Terms and Conditions
          </label>
          <button>Submit</button>
        </Form>
      </div>
    </Formik>
  )
}

const FormikApp = withFormik({
  mapPropsToValues({ name, email, password, terms }) {
    return {
      name: name || '',
      email: email || '',
      password: password || '',
      terms: terms || false
    }
  },
  validationSchema: Yup.object().shape({
    name: Yup.string()
      .name()
      .required(),
    email: Yup.string()
      .email()
      .required(),
    password: Yup.string()
      .min(6)
      .required(),
    terms: Yup.string()
      .terms()
      .required()
  }),
  handleSubmit(values) {
    console.log(values)
  }
})

const MikFor = FormikApp(AppForm)

export default MikFor
