import React, { useState, useEffect } from 'react'
import { Form, Field, withFormik } from 'formik'
import * as Yup from 'yup'
import axios from 'axios'

const AppForm = ({ errors, touched, values, status }) => {
  const [state, set] = useState([])
  console.log(state)

  useEffect(() => {
    if (status) {
      set([...state, status])
    }
  }, [status])

  return (
    <div className='animal-form'>
      <Form>
        <Field type='text' name='name' placeholder='name' />
        {touched.name && errors.name && <p className='error'>{errors.name}</p>}
        {touched.password && errors.password && (
          <p className='error'>{errors.password}</p>
        )}
        <Field type='text' name='password' placeholder='Password' />
        <label className='checkbox-container'>
          <Field type='checkbox' name='term' checked={values.term} />I agree to
          the Terms and Conditions
        </label>
        <button>Submit!</button>
      </Form>
      {state.map(e => (
        <p key={e.id}>{e.name}</p>
      ))}
    </div>
  )
}

const formikHOC = withFormik({
  mapPropsToValues({ name, password, term }) {
    return {
      name: name || '',
      password: password || '',
      term: term || false
    }
  },
  validationSchema: Yup.object().shape({
    name: Yup.string().required('not a good input'),
    password: Yup.number().required()
  }),
  handleSubmit(values, { setStatus, resetForm }) {
    axios
      .post('https://reqres.in/api/users', values)
      .then(res => {
        console.log('handleSubmit: then: res: ', res)
        setStatus(res.data)
        resetForm()
      })
      .catch(err => console.error('handleSubmit: catch: err: ', err))
  }
})
const AppWithMik = formikHOC(AppForm)

export default AppWithMik
